package B2_비교연산자_기본이론;

public class 비교연산자_프로젝트_부자_문제 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*
		 [문제]
		   철수는 13살 철수의 아버지는 45살이다. 
		   몇년후 철수의 아버지는 철수나이의 3배가될까요?
		*/
		int 철수 = 13;
		int 아버지 = 45;
		
		int a = 3;		
		System.out.println((철수 + a)*3 == 아버지 + a);
	}
}
